import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { diagrams, chat, auth } from '../api';
import Canvas from './Canvas';

const DiagramEditor = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [activeTool, setActiveTool] = useState('pen');
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (!auth.isAuthenticated()) {
      navigate('/');
      return;
    }
    const currentUser = auth.getUser();
    setUser(currentUser);
  }, [navigate]);

  const handleLogout = async () => {
    await auth.logout();
    navigate('/');
  };

  return (
    <div className="flex flex-col h-screen bg-white">
      <header className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
        <div className="flex items-center gap-4">
          <Link to="/diagrams" className="text-blue-600 hover:text-blue-800">
            ‚Üê Back to Diagrams
          </Link>
          <h1 className="text-xl font-bold text-gray-800">Diagram Editor</h1>
        </div>
        <div className="flex items-center gap-4">
          {user && (
            <>
              <span className="text-sm text-gray-600">Welcome, {user.username}</span>
              <button
                onClick={handleLogout}
                className="px-3 py-1 text-sm text-white bg-red-500 rounded hover:bg-red-600"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </header>

      <div className="flex items-center gap-4 px-6 py-3 bg-gray-50 border-b border-gray-200">
        {['pen', 'rect', 'circle', 'line', 'text', 'select'].map((tool) => (
          <button
            key={tool}
            onClick={() => setActiveTool(tool)}
            className={`px-3 py-2 text-sm font-medium rounded ${
              activeTool === tool 
                ? 'bg-blue-500 text-white' 
                : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            {tool.charAt(0).toUpperCase() + tool.slice(1)}
          </button>
        ))}
      </div>

      <div className="flex-1 p-6">
        <Canvas activeTool={activeTool} diagramId={id} />
      </div>
    </div>
  );
};

export default DiagramEditor;
